mapper.select:
select

from t_log_file


        and date_time > #{startDate}


        and date_time < #{endDate}


        and service_name = #{serviceName}


        and trace_id = #{traceId}


        and sub_trace_id = #{subTraceId}


        and `level` = #{level}


        and tran_code = #{tranCode}


        and content like '%' || #{content} || '%'

mapper.delete:
delete from t_log_file


        and date_time > #{startDate}


        and date_time < #{endDate}


        and service_name = #{serviceName}


        and trace_id = #{traceId}


        and sub_trace_id = #{subTraceId}


        and `level` = #{level}


        and tran_code = #{tranCode}


        and content like '%' || #{content} || '%'
