mapper.insert:

insert into t_user (ID, USERNAME, NICKNAME,
  PASSWORD, ROLE_NO, CREATE_TIME, 
  CREATE_USER, UPDATE_TIME, UPDATE_USER
  )
values (#{id,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{nickname,jdbcType=VARCHAR},
  #{password,jdbcType=VARCHAR}, #{roleNo,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}
  )

mapper.select:

select ID, USERNAME, NICKNAME, PASSWORD, ROLE_NO, CREATE_TIME, CREATE_USER, UPDATE_TIME,
UPDATE_USER
from t_user where USERNAME = #{username,jdbcType=VARCHAR}


select ID, USERNAME, NICKNAME, PASSWORD, ROLE_NO, CREATE_TIME, CREATE_USER, UPDATE_TIME,
UPDATE_USER
from t_user


    AND USERNAME = #{username,jdbcType=VARCHAR}


    AND NICKNAME = #{nickname,jdbcType=VARCHAR}


    AND ROLE_NO = #{roleNo,jdbcType=VARCHAR}
